import React, { useState } from "react";

import "./NoteLayout.css";
import Sidebar from "./Sidebar";
import SelectedNote from "./SelectedNote";

const NoteLayout = () => {
  const [notes, setNotes] = useState([
    {
      header: "My Special Note 1",
      timestamp: "Last Updated: 1:37PM 7/26/19",
      body: "You might remember the Dell computer commercials in which a youth reports this exciting news to his friends that they are about to get their new computer by telling them, “Dude, you’re getting a Dell!” It was a cute series but it reflects the excitement young people get about anything new, particularly if it’s a new machine.",
    },
    {
      header: "My Special Note 2",
      timestamp: "Last Updated: 1:37PM 7/26/20",
      body: "You might remember the Dell computer commercials in which a youth reports this exciting news to his friends that they are about to get their new computer by telling them, “Dude, you’re getting a Dell!” It was a cute series but it reflects the excitement young people get about anything new, particularly if it’s a new machine.",
    },
    {
      header: "My Special Note 3",
      timestamp: "Last Updated: 1:37PM 7/26/20",
      body: "You might remember the Dell computer commercials in which a youth reports this exciting news to his friends that they are about to get their new computer by telling them, “Dude, you’re getting a Dell!” It was a cute series but it reflects the excitement young people get about anything new, particularly if it’s a new machine.",
    },
  ]);
  const [activeNote, setActiveNote] = useState(0);

  const handleNotesSelect = (i) => {
    setActiveNote(i);
  };

  return (
    <section className="layout">
        <p>{activeNote}</p>
      <Sidebar notes={notes} onNoteSelect={handleNotesSelect} />
      {isSelected && <SelectedNote />}
      {!isSelected && <p>Welcome to Your Notes!</p>}
    </section>
  );
};

export default NoteLayout;
