import React from "react";

import "./Sidebar.css";

const Sidebar = (props) => {
  const existingNotes = [
    {
      header: "My Special Note 1",
      timestamp: "Last Updated: 1:37PM 7/26/19",
      body: "You might remember the Dell computer commercials in which a youth reports this exciting news to his friends that they are about to get their new computer by telling them, “Dude, you’re getting a Dell!” It was a cute series but it reflects the excitement young people get about anything new, particularly if it’s a new machine.",
    },
    {
      header: "My Special Note 2",
      timestamp: "Last Updated: 1:37PM 7/26/20",
      body: "You might remember the Dell computer commercials in which a youth reports this exciting news to his friends that they are about to get their new computer by telling them, “Dude, you’re getting a Dell!” It was a cute series but it reflects the excitement young people get about anything new, particularly if it’s a new machine.",
    },
  ];

  return (
    <aside class="sidebar">
      <button type="button" class="button block tertiary header-text">
        Create Note
      </button>
      <ul>
          {props.notes.map((existingNotes) => (
            <li class="active">
            <h3 class="header-text no-margin">{props.header}</h3>
            <p class="body-text no-margin muted">{props.timestamp}</p>
          </li>  
          ))}
        {/* <li class="active">
          <h3 class="header-text no-margin">{existingNotes[0].header}</h3>
          <p class="body-text no-margin muted">{existingNotes[0].timestamp}</p>
        </li>
        <li>
          <h3 class="header-text no-margin">{existingNotes[1].header}</h3>
          <p class="body-text no-margin muted">{existingNotes[1].timestamp}</p>
        </li> */}
      </ul>
    </aside>
  );
};

export default Sidebar;
